import React, { useState} from 'react';
import axios from 'axios';
import { Container, Row, Col, Form, FormControl, Button } from 'react-bootstrap';
import Cabecalho from '../componentes/cabecalho';
import './styles.css'


function ListaSalas() {
  
  const [tableData, setTableData] = useState([]);

  const handleSubmit = (event) => {
    event.preventDefault();    
    
    const searchTerm = {tags:event.target.search.value};
    console.log('Pesquisar por:', searchTerm);
    
    const fetchTableData = async () => {
        
        try {
          
          const response = await axios( {
            method: 'get',
            url: 'http://localhost:5000/api/salas',
            headers: {
              'Content-Type': 'application/json'
            },
            data: searchTerm
    
        });
        setTableData(response.data);   
        } catch (error) {
          console.error(error);
        }
    
        }
    fetchTableData();
    };
  
  return (    

    <Container fluid>
      <Row>
        <Col xs={12}>
        <Cabecalho />
        </Col>
      </Row>
      <Row>

        <Form onSubmit={handleSubmit} className="form-pesquisa">
                <FormControl
                type="text"
                placeholder="Pesquisar"
                name='search'
                />
                <Button variant="outline-success" type="submit">Pesquisar</Button>
        </Form>
        <Row>           
                <div className="table-container">
                    <div className="shadow bg-light border-primary text-center">
                        <h2>Salas</h2>
                    </div>
                    <table className='striped bordered hover'>
                        <thead>
                            <tr>
                                <th>Número</th>
                                <th>Tipo</th>
                                <th>Valor</th>
                                <th>Capacidade</th>
                                <th>Descrição</th>
                            </tr>
                        </thead>
                        <tbody>
                            {tableData.map((row, index) => {
                                return (
                            <tr key={index} data-toogle="tooltip" title={row.numero}>
                                <td>{row.numero}</td>
                                <td>{row.tipo}</td>
                                <td>{row.valor}</td>
                                <td>{row.capacidade}</td>
                                <td>{row.descricao}</td>
                            </tr>
                            );
                            })}
                        </tbody>
                    </table>
                </div>
      </Row> 
      </Row>
             
    

    </Container>
  );
}

export default  ListaSalas ;
