import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row  } from 'react-bootstrap';
import { useParams } from "react-router-dom";
import Cabecalho from '../componentes/cabecalho';
import 'bootstrap/dist/css/bootstrap.min.css';
//import './styles.css'


function Salas() {
  
  const { id } = useParams();
  const [sala, setFormData] = useState([]);
  
  useEffect(() => {
      async function fetchFormData () {
      
      try {
        
        console.log('id =' + id);
        const response = await axios.get(`http://localhost:5000/api/salas/${id}`);
        setFormData(response.data);      
        console.log('response ' + response.data);
      } catch (error) {
        console.error(error);
      }
  
      };
      fetchFormData();
    },[id]); 
  
  return (    

    <Container fluid>
      <Row>
        <Cabecalho />
      </Row>
      <Row>
        <div>
        <form className='formEdit' action="<%= action %>" method="POST">
          <div>
          <label>Numero: </label> <input type="text" name="numero" value={sala.numero} />
          </div>
          <div>
          <label>Tipo: </label> <input type="text" name="tipo" value={sala.tipo} />
          </div>
          <div>
            <label>Capacidade: </label>  <input type="text" name="capacidade" value={sala.capacidade} />
          </div>
          <div>
          <label>Valor: </label>   <input type="text" name="valor" value={sala.valor} />
          </div>
          <div>
            <label>Descrição: </label> <input type="text" name="descricao" value={sala.descricao} />
          </div><br/>
          <div>
          <input type="submit" value="RESERVAR" />
          </div>
        </form>
        </div>

        <img className="tamanho2" src={sala.image} alt="imagem banco"></img>
      </Row>         
    </Container>
  );
}

export default Salas;
