import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row  } from 'react-bootstrap';
import { useParams } from "react-router-dom";
import Cabecalho from '../componentes/cabecalho';
import 'bootstrap/dist/css/bootstrap.min.css';
//import './styles.css'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';


function Salas() {
  
  const { id } = useParams();
  const [sala, setFormData] = useState([]);
  
  useEffect(() => {
      async function fetchFormData () {
      
      try {
        
        console.log('id =' + id);
        const response = await axios.get(`http://localhost:5000/api/salas/${id}`);
        setFormData(response.data);      
        console.log('response ' + response.data);
      } catch (error) {
        console.error(error);
      }
  
      };
      fetchFormData();
    },[id]); 
  
  return (    

    <Container classname="kkk" fluid>
      <Row>
        <Cabecalho />
      </Row>
   
      <Form >
       <div className='todo'>
      <Form.Group className="mb-2" controlId="formBasicEmail">
        <Form.Label> Número: <Form.Control  type="text" name="numero" value={sala.numero} /> </Form.Label>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label> Tipo: <Form.Control type="text" name="tipo" value={sala.tipo} /></Form.Label>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label> Capacidade:  <Form.Control type="text" name="capacidade" value={sala.capacidade} /></Form.Label>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label> Valor: <Form.Control type="text" name="valor" value={sala.valor} /></Form.Label>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label> Descrição: <Form.Control type="text" name="descricao" value={sala.descricao} /></Form.Label>
      </Form.Group>
      
      <Button bg="light" variant="warning" size="lg" type="submit">
       Reservar agora
      </Button>
      </div>
      <img className="tamanho2" src={sala.image} alt="imagem banco"></img>
    </Form>
    
            
    </Container>
  );
}

export default Salas;
