import React, { useState} from 'react';
import axios from 'axios';
import { Container, Row, Col, Form, FormControl, Button, Image} from 'react-bootstrap';
import { Link } from "react-router-dom";
import Cabecalho from '../componentes/cabecalho';
import './styles.css'


function ListaSalas() {
  
  const [tableData, setTableData] = useState([]);

  const handleSubmit = (event) => {
    event.preventDefault();    
    
    const searchTerm = {tags:event.target.search.value};
    console.log('Pesquisar por:', searchTerm);
    
    const fetchTableData = async () => {
        
        try {
          
          const response = await axios( {
            method: 'get',
            url: 'http://localhost:5000/api/salas',
            headers: {
              'Content-Type': 'application/json'
            },
            data: searchTerm
    
        });
        setTableData(response.data);   
        } catch (error) {
          console.error(error);
        }
    
        }
    fetchTableData();
    };

    async function handleDelete(id) {
      try {
             await axios.delete(`http://localhost:5000/api/salas/${id}`);
            alert(id);
      } catch (error) {
        console.error(error);
      }      
    }
  
  return (    

    <Container fluid>
      <Row>
        <Col xs={12}>
        <Cabecalho />
        </Col>
      </Row>
        <Form onSubmit={handleSubmit} className="form-pesquisa">
                <FormControl
                type="text"
                placeholder="Pesquisar"
                name='search'
                />
                <Button variant="outline-success" type="submit">Pesquisar</Button>
                <Row className='headerSalas'>SALAS</Row>
                    <table className='salasTab'>
                        <thead>
                            <tr>
                                <th>Número</th>
                                <th>Tipo</th>
                                <th>Valor</th>
                                <th>Capacidade</th>
                                <th>Descrição</th>
                            </tr>
                         
                        </thead>
                        <tbody>
                            {tableData.map((row, index) => {
                                return (
                            <tr key={index} data-toogle="tooltip" title={row.numero}>
                                <td>{row.numero}</td>
                                <td>{row.tipo}</td>
                                <td>{row.valor}</td>
                                <td>{row.capacidade}</td>
                                <td>{row.descricao}</td>
                                <td>
                                  <Link to={`/salas/${row._id}`} >
                                    <Image
                                          src="https://cdn-icons-png.flaticon.com/512/1159/1159633.png"
                                          alt="editar"
                                          className="rounded-circle"
                                          width="30"
                                          height="30"
                                    />
                                  </Link>
                                </td>
                                <td>
                                    <Image 
                                      src="https://cdn-icons-png.flaticon.com/512/3405/3405244.png"
                                      alt="apagar"
                                      className="rounded-circle"
                                      width="30"
                                      height="30"
                                      onClick={() => handleDelete(row._id)}
                                    />
                                </td>
                            </tr>
                            );
                            })}
                        </tbody>
                    </table>
        </Form>
    </Container>
  );
}

export default  ListaSalas ;
