import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row  } from 'react-bootstrap';
import { useParams, useNavigate } from "react-router-dom";
import Cabecalho from '../componentes/cabecalho';
import 'bootstrap/dist/css/bootstrap.min.css';
//import './styles.css'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';


function Salas() {
  
  const { id } = useParams();
  const [sala, setFormData] = useState([]);
  const history = useNavigate();
  
  useEffect(() => {
      async function fetchFormData () {
      
      try {
        
        console.log('id =' + id);
        const response = await axios.get(`http://localhost:5000/api/salas/${id}`);
        setFormData(response.data);      
        console.log('response ' + response.data);
      } catch (error) {
        console.error(error);
      }
  
      };
      fetchFormData();
    },[id]); 

    const handleSubmit = async (event) => {
      event.preventDefault();
      try {
        
        //const id = event.target._id.value;
        if (event.nativeEvent.submitter.name === "salvar") {
          alert(id);
          if (id.length === 0) {
              await axios.post('http://localhost:5000/api/salas/',sala );
              alert('incluido com sucesso!'); 
          }
          else {
              await axios.put(`http://localhost:5000/api/salas/${id}`,sala );
              alert('alterado com sucesso!');
          }
        }
      } catch (error) {
        console.error(error);
      }
      history(-1);
    }

    const handleChange = (event) => {
      const { name, value } = event.target;
      setFormData({ ...sala, [name]: value });
    };
  
  
  return (    

    <Container  fluid>
      <Row>
        <Cabecalho />
      </Row>
   
      <Form onSubmit={handleSubmit}>
       <div className='todo'>
      <Form.Group className="mb-2" controlId="formBasicEmail">
        <Form.Label> Número: <Form.Control  type="text" name="numero" value={sala.numero} onChange={handleChange} /> </Form.Label>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label> Tipo: <Form.Control type="text" name="tipo" value={sala.tipo} onChange={handleChange} /></Form.Label>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label> Capacidade:  <Form.Control type="text" name="capacidade" value={sala.capacidade} onChange={handleChange} /></Form.Label>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label> Valor: <Form.Control type="text" name="valor" value={sala.valor} onChange={handleChange} /></Form.Label>
      </Form.Group>
      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label> Descrição: <Form.Control type="text" name="descricao" value={sala.descricao} onChange={handleChange} /></Form.Label>
      </Form.Group>
      
      <Button size="lg" type="submit">
       Reservar agora
      </Button>
      </div>
      <img className="tamanho2" src={sala.image} alt="imagem banco"></img>
    </Form>
    
            
    </Container>
  );
}

export default Salas;
