import {React, useState, useEffect} from 'react';
import { Carousel, Container, Row, Col, Card } from 'react-bootstrap';
import axios from 'axios';
import Cabecalho from './cabecalho';

function Carrossel() {

    const [carouselData, setCaroselData] = useState([]);
  
    useEffect(() => {
      async function fetchCarouselData () {
      
      try {
        
        const response = await axios.get('http://localhost:5000/api/salas');
        setCaroselData(response.data);
      } catch (error) {
        console.error(error);
      }
  
      };
      fetchCarouselData();
    },[]);

    return (
      
        <Container fluid>

        <Row>
        <Col xs={12}>
            <Cabecalho />
        </Col>
        </Row>
        <Row>

        <Card>            
            <Card.Body>
                <Card.Title></Card.Title>
                <Card.Text>
                    <Carousel>        
                        {carouselData.keys().map((carousel) => (
                        <Carousel.Item key={carousel.id}><img key={carousel} src={carouselData(carousel.imagem)} alt="" /></Carousel.Item>
                        ))}
                    </Carousel>      
                </Card.Text>          
            </Card.Body>
        </Card>

        </Row>

        </Container>

    );
  }
  
  export default Carrossel;
