import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Row  } from 'react-bootstrap';
import { useParams } from "react-router-dom";
import Cabecalho from '../componentes/cabecalho';
import 'bootstrap/dist/css/bootstrap.min.css';


function Salas() {
  
  const { id } = useParams();
  const [sala, setFormData] = useState([]);
  
  useEffect(() => {
      async function fetchFormData () {
      
      try {
        
        console.log('id =' + id);
        const response = await axios.get(`http://localhost:5000/api/salas/${id}`);
        setFormData(response.data);      
        console.log('response ' + response.data);
      } catch (error) {
        console.error(error);
      }
  
      };
      fetchFormData();
    },[id]); 
  
  return (    

    <Container fluid>
      <Row>
        <Cabecalho />
      </Row>
     
      <Row>
    
        <div className="formEdit">
        <form action="<%= action %>" method="POST">
          <div>
          <p>Numero: <input type="text" name="numero" value={sala.numero} /></p>
          </div>
          <div>
          <p>Tipo:</p>
              <input type="text" name="tipo" value={sala.tipo} />
          </div>
          <div>
            <p>Capacidade:</p>
              <input type="text" name="capacidade" value={sala.capacidade} />
          </div>
          <div>
          <p>Valor:</p>
            <input type="text" name="valor" value={sala.valor} />
          </div>
          <div>
            <p>Descrição:</p>
              <input type="text" name="descricao" value={sala.descricao} />
          </div><br/>
          <div>
          <input type="submit" value="RESERVAR" />
          </div>
        </form>
        </div>

        <img className="tamanho2" src={sala.image} alt="imagem banco"></img>
       
     
      </Row>         

    </Container>
  );
}

export default Salas;
