import {React, useState, useEffect} from 'react';
import { Carousel, Container, Row, Card } from 'react-bootstrap';
import axios from 'axios';
//import '../styles.css'

//import Cabecalho from '../componentes/cabecalho';

function Carrossel() {

    const [carrosselData, setCarrosselData] = useState([]);
  
    useEffect(() => {
      async function fetchCarrosselData () {
      
      try {
        
        const response = await axios.get('http://localhost:5000/api/salas');
        setCarrosselData(response.data);
        //console.log('data' + carrosselData);
        //console.log('response' + response);
      } catch (error) {
        console.error(error);
      }
  
      };
      fetchCarrosselData();
    },[carrosselData]);

    return (
      
        <Container fluid>
        <Row>
        <Card>            
            <Card.Body>
                <Card.Title></Card.Title>
                <Card.Text>
                    <Carousel>        
                        {carrosselData.map((item, index) => (
                        <Carousel.Item key={item.id}><img className="tamanho" key={index} src={item.image} alt="" /></Carousel.Item>
                        ))}
                    </Carousel>
                   
                </Card.Text>          
            </Card.Body>
        </Card>
        </Row>
        </Container>

    );
  }
  
  export default Carrossel;
